import { ReactNode } from 'react';
/**
 * Event object emitted when a drag and drop operation is completed.
 */
export type DndSortChangeEvent = {
    /**
     * The ID of the draggable item that was dropped.
     */
    draggableId: string | number;
    /**
     * The ID of the drop zone where the item was dropped.
     * This can be null if the item was not dropped in a zone.
     */
    zoneId: string | number | null;
    /**
     * The ID of the previous drop zone where the item was located before being dropped.
     * This can be null if the item is coming from outside of any zone.
     */
    previousZoneId: string | number | null;
    /**
     * Indicates whether the drop was valid based on the accepted types and custom validation.
     */
    valid: boolean;
    /**
     * An array representing the sort order of items in the drop zone after the drop.
     * This can be used to update the order of items in the zone.
     *
     * This value is null if the target drop zone (if any) is not sortable.
     */
    zoneSort: (string | number)[] | null;
    /**
     * An array representing the sort order of items in the previous drop zone before the drop.
     * This can be used to update the order of items in the previous zone.
     *
     * This value is null if the previous drop zone (if any) was not sortable.
     */
    previousZoneSort: (string | number)[] | null;
};
/**
 * Function type for advanced zone rendering with state parameters.
 */
export type DndSortZoneAdvancedRenderer = (params: {
    /**
     * Whether any item is currently being dragged.
     */
    isDragging: boolean;
    /**
     * Whether a draggable item is currently over this zone.
     */
    isOver: boolean;
    /**
     * Whether the current drag operation is valid for this zone.
     */
    isValid: boolean;
}) => ReactNode;
