/**
 * This function applies the mask to the input value, returning a new string that matches the mask's format.
 * @param value - a string representing the raw input value (e.g., what the user typed)
 * @param mask -  a string representing the desired input mask (e.g., "99/99/9999" for a date)
 * @param defaultMaskCharacter - a string representing the default mask character (e.g., "_")
 * @returns a string representing the masked value (e.g., "12/31/2025" for a date)
 *
 * @example
 * applyMask("1234567890", "99/99/9999", "_") // "12/31/2025"
 * applyMask("1234567890", "999-999-9999", "-") // "123-456-7890"
 * applyMask("123", "99/99", "_") // "12/3_"
 */
export declare function applyMask(value: string, mask: string, defaultMaskCharacter: string): string;
