/**
 * Time format options for the TimeField component.
 */
export type TimeFormat = 12 | 24;
/**
 * Change event data for TimeField onChange callback.
 */
export interface TimeFieldChange {
    /**
     * The time in HH:mm format (24-hour) or hh:mm a format (12-hour).
     * @example "14:30" or "02:30 PM"
     */
    time: string | null;
    /**
     * Whether the input is valid. That is, it matches the format and is not empty.
     * This does not mean that the time is valid according to the constraints.
     */
    isInputValid: boolean;
    /**
     * Whether the input is empty.
     */
    isInputEmpty: boolean;
}
/**
 * Callback function type for TimeField onChange events.
 */
export type TimeFieldChangeHandler = (change: TimeFieldChange) => void;
