import { ToastProps } from './Toast';
import { toastProps } from '../toast';
export type ToastUpdateProps = Partial<toastProps> & {
    id: string;
    status?: ToastProps["status"];
};
export type ToastCustomEventDetailAdd = ToastProps & {
    eventType: "add";
    toasterId?: string;
};
export type ToastCustomEventDetailUpdate = ToastUpdateProps & {
    eventType: "update";
};
export type ToastCustomEventDetailDismiss = {
    id?: string;
    eventType: "dismiss";
};
export type ToastCustomEventDetail = ToastCustomEventDetailAdd | ToastCustomEventDetailUpdate | ToastCustomEventDetailDismiss;
