export type InputMode = "decimal" | "numeric" | "text";
/**
 * Custom hook that determines the optimal inputMode attribute for number fields based on platform and requirements.
 *
 * The inputMode attribute tells the device what type of software keyboard to show.
 * There are inconsistencies in what each software keyboard includes across different platforms:
 * - iPhone lacks a minus sign in both numeric and decimal keyboards
 * - Android numeric keyboard has both decimal point and minus sign
 * - iPad has different keyboard behavior than iPhone
 *
 * @param hasDecimals - Whether the number field allows decimal values
 * @param hasNegative - Whether the number field allows negative values
 * @returns The optimal inputMode attribute to use on the number field
 *
 * @example
 * const inputMode = useNumberFieldInputMode({
 *   hasDecimals: true,
 *   hasNegative: false
 * });
 */
export declare const useNumberFieldInputMode: ({ hasDecimals, hasNegative, }: {
    hasDecimals: boolean;
    hasNegative: boolean;
}) => InputMode;
