/**
 * Global layer manager for tracking overlay component stacking order.
 * Uses window-level state to support cross-bundle communication (e.g., MFEs).
 *
 * When multiple Dialog/Drawer components are open, only the topmost layer
 * should respond to outside click events. This manager tracks the z-order
 * to enable that behavior.
 */
declare const WINDOW_PROPERTY_LAYER_STACK: "__ANVIL_LAYER_MANAGER__";
declare global {
    interface Window {
        [WINDOW_PROPERTY_LAYER_STACK]?: string[];
    }
}
/**
 * Register a new layer in the stack.
 * If the layer is already registered, this is a no-op.
 *
 * @param id - Unique identifier for the layer
 */
export declare const registerLayer: (id: string) => void;
/**
 * Unregister a layer from the stack.
 * If the layer is not found, this is a no-op.
 *
 * @param id - Unique identifier for the layer
 */
export declare const unregisterLayer: (id: string) => void;
/**
 * Check if a layer is the topmost layer in the stack.
 *
 * @param id - Unique identifier for the layer
 * @returns True if the layer is on top, false otherwise
 */
export declare const isTopLayer: (id: string) => boolean;
/**
 * Get all registered layers in order (bottom to top).
 * Returns a readonly copy for debugging purposes.
 *
 * @returns Readonly array of layer IDs
 */
export declare const getLayers: () => readonly string[];
export {};
